import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { Person } from '../models/personnel.model';

@Injectable({ providedIn: 'root' })
export class PersonService {
  private persons: Person[] = [
   
    // diğer personeller
  ];

  private personsSubject = new BehaviorSubject<Person[]>(this.persons);

  getPersons(): Observable<Person[]> {
    return this.personsSubject.asObservable();
  }

  addPerson(person: Person) {
    this.persons.push({ ...person, id: this.persons.length + 1 });
    this.personsSubject.next(this.persons);
  }

  updatePerson(updated: Person) {
    const index = this.persons.findIndex(p => p.id === updated.id);
    if (index !== -1) {
      this.persons[index] = { ...updated };
      this.personsSubject.next(this.persons);
    }
  }

  deletePerson(id: number) {
    this.persons = this.persons.filter(p => p.id !== id);
    this.personsSubject.next(this.persons);
  }
}
